export default styleInject;
import { ReactNode, memo } from 'react'
import styles from '../styles.module.css'

export type CursorProps = {
  /** Enable cursor blinking animation */
  cursorBlinking?: boolean
  /** Change cursor style */
  cursorStyle?: ReactNode
  /** Change cursor color */
  cursorColor?: string
}

const MemoizedCursor = ({
  cursorBlinking = true,
  cursorStyle = '|',
  cursorColor = 'inherit'
}: CursorProps): JSX.Element => {
  return (
    <span
      style={{ color: cursorColor }}
      className={`${styles.blinkingCursor} ${
        cursorBlinking ? styles.blinking : ''
      }`}
    >
      {cursorStyle}
    </span>
  )
}

export const Cursor = memo(MemoizedCursor)
import { TypewriterProps, useTypewriter } from '../hooks/useTypewriter'
import { Cursor, CursorProps } from './Cursor'

type ComponentProps = {
  /** Show / Hide the cursor */
  cursor?: boolean
} & TypewriterProps &
  CursorProps

/**
 * A Simple React Component for adding a nice Typewriter effect to your project.
 */
export const Typewriter = ({
  words = ['Hello World!', 'This is', 'a simple Typewriter'],
  loop = 1,
  typeSpeed = 80,
  deleteSpeed = 50,
  delaySpeed = 1500,
  cursor = false,
  cursorStyle = '|',
  cursorColor = 'inherit',
  cursorBlinking = true,
  onLoopDone,
  onType,
  onDelay,
  onDelete
}: ComponentProps): JSX.Element => {
  const [text] = useTypewriter({
    words,
    loop,
    typeSpeed,
    deleteSpeed,
    delaySpeed,
    onLoopDone,
    onType,
    onDelay,
    onDelete
  })

  return (
    <>
      <span>{text}</span>
      {cursor && (
        <Cursor
          cursorStyle={cursorStyle}
          cursorColor={cursorColor}
          cursorBlinking={cursorBlinking}
        />
      )}
    </>
  )
}
